import React from "react"
import { ImageBackground, View } from "react-native"

Login = () => {
    return (
        <View style={{flex: 1}}> 
         <ImageBackground source={require("../../utils/image/login_img/background_screen2.png")}
         resizeMode="cover"
         style={{height: '40%'}}>

         </ImageBackground>
        </View>
    )
}
export default Login
